INSERT INTO `foot_2009_match` (`kp_match`, `lieu`, `date`, `ke_equipe_dom`, `ke_equipe_ext`, `resultat`, `score_eq_dom`, `score_eq_ext`, `ke_journee`, `Inscription`, `commentaire`, `pos_ATT`, `pos_MIL_C`, `pos_MIL_G`, `pos_MIL_D`, `pos_DEF_G`, `pos_DEF_D`, `pos_GB`) VALUES
(5, 'Saint-Laurent d''Agny', 1251394200, 2, 1, 3, '4', '6', 1, 11, '', 'L Buffevant;F Magnan', 'Tonio le Chat;R Belu', 'R Belu;F Magnan', 'G Grech;S Falcon', 'M Corrand;D Bourette', 'Rico;S Falcon', 'S Perret;'),
(6, 'Saint-Laurent d''Agny', 1252431000, 2, 1, 3, '4', '7', 1, 9, '<p>\r\n	Bref d&eacute;tail de la rencontre (avant explication complete).<br />\r\n	Configuration de match de championnat (2 mi temps de 30-35).</p>\r\n<p>\r\n	1-0 pour la fuzion 1.</p>\r\n<p>\r\n	1-1 sur un but de Laurentsur un contre amorc&eacute; par rico, laurent trompe le gardien d&#39;une demi vol&eacute;e dans le petit filet.</p>\r\n<p>\r\n	2-1 pour la fuzion 1.</p>\r\n<p>\r\n	Egalisation de Laurent sur un contre via une passe de aymeric, Laurent frappe repouss&eacute; par le gardien mais revient dans les pieds de l&#39;attaquant qui marque.(2-2)</p>\r\n<p>\r\n	Mi temps.</p>\r\n<p>\r\n	3-2 pour la fuzion 1.</p>\r\n<p>\r\n	3-3 Sur une egalisation de viv suite a une mauvaise relance du defenseur adverse viv reprend le ballon et trompe facilement le gardien, suivi directement du 4e but pour la Fuzion 2 suite a un but contre son camp du meme defenseur.(3-4)</p>\r\n<p>\r\n	3-5 Sur une mauvaise passe d&#39;un defenseur adverse laurent intercepte la balle et trompe le gardien.</p>\r\n<p>\r\n	4-5.</p>\r\n<p>\r\n	4-6 Suite a une relance de rico laurent se presente excentr&eacute; face au gardien mais parvient a l&#39;ajust&eacute; avec l&#39;aide du poteau.</p>\r\n<p>\r\n	Puis 4-7 Sur un nouveau contre une remise de la tete d&#39;aymeric repris de vol&eacute; par Laurent qui marquera son 5e et dernier but de la soir&eacute;e.</p>\r\n<p>\r\n	Le match que l&#39;on calfiera d&#39;officiel se finit sur ce score.</p>\r\n<p>\r\n	Nous avons r&eacute;alis&eacute; une 3e mi-temps (que nous ne compterons pas vu notre faible niveau lors de celle-ci) ou nous avons perdu 5-0. Un gros relachement de l&#39;&eacute;quipe a provoqu&eacute; cette petite d&eacute;route finale qui laisse un petit gout amer pour se match.</p>\r\n<p>\r\n	Cependant l&#39;&eacute;quipe a prouver de bonnes choses pendant plus d&#39;une heure.</p>\r\n<p>\r\n	Bien jou&eacute; les gars</p>\r\n<p>\r\n	LB</p>\r\n', 'L Buffevant;A Dubost', 'Tonio le Chat;A Dubost', 'Rico;M Corrand', 'D Bourette;A Dubost', 'M Corrand;', 'S Magnin;D Dal Gobbo', 'S Perret;'),
